
public class length {

	public static void main(String[] args) {
		// TODO Auto-generated constructor stub
		
		char chars[] = {'H','E','L','L','O','W','O','R','L','D'};
		
		String str = new String(chars);
		
		System.out.println(str + " has ");
		
		System.out.println(str.length()+" length ");
	}

}
